+import { useState } from "react";
+
+// react-router-dom components
+import { Link } from "react-router-dom";
+
+import Switch from "@mui/material/Switch";
+import axios from "axios";
+
+// Material Kit 2 React components
+import MKBox from "components/MKBox";
+import MKTypography from "components/MKTypography";
+import MKInput from "components/MKInput";
+import MKButton from "components/MKButton";
+
+function SignInBasic() {
+  const [user, setUser] = useState({
+    email: "",
+    password: "",
+  });
+  const [loginError, setLoginError] = useState(null);
+
+  const handleSubmit = async (e) => {
+    e.preventDefault();
+
+    const userData = {
+      email: user.email,
+      password: user.password,
+    };
+    try {
+      const loginRes = await axios.post("http://localhost:5000/login", userData, {
+        headers: { "Content-Type": "application/json" },
+      });
+
+      if (loginRes.status === 200) {
+        // Handle successful login
+        setLoginError(null); // Clear any previous login error
+      }
+    } catch (err) {
+      // Handle login failure and set appropriate error message
+      if (err.response.status === 401) {
+        setLoginError("Incorrect email or password.");
+      } else {
+        setLoginError("An error occurred while logging in.");
+      }
+    }
+  };
+
+  const handleInputChange = (e) => {
+    const { name, value } = e.target;
+    setUser({
+      ...user,
+      [name]: value,
+    });
+  };
+
+  const [rememberMe, setRememberMe] = useState(false);
+
+  const handleSetRememberMe = () => setRememberMe(!rememberMe);
+
+  return (
+    <>
+      {/* ... (existing JSX code remains the same) */}
+      <MKBox pt={4} pb={3} px={3}>
+        <MKBox component="form" role="form" onSubmit={handleSubmit}>
+          <MKBox mb={2}>
+            <MKInput
+              type="email"
+              label="Email"
+              fullWidth
+              name="email"
+              value={user.email}
+              onChange={handleInputChange}
+            />
+          </MKBox>
+          <MKBox mb={2}>
+            <MKInput
+              type="password"
+              label="Password"
+              fullWidth
+              name="password"
+              value={user.password}
+              onChange={handleInputChange}
+            />
+          </MKBox>
+          <MKBox display="flex" alignItems="center" ml={-1}>
+            <Switch checked={rememberMe} onChange={handleSetRememberMe} />
+            <MKTypography
+              variant="button"
+              fontWeight="regular"
+              color="text"
+              onClick={handleSetRememberMe}
+              sx={{ cursor: "pointer", userSelect: "none", ml: -1 }}
+            >
+              &nbsp;&nbsp;Remember me
+            </MKTypography>
+          </MKBox>
+          <MKBox mt={4} mb={1}>
+            <MKButton type="submit" variant="gradient" color="info" fullWidth>
+              Sign in
+            </MKButton>
+          </MKBox>
+          <MKBox mt={3} mb={1} textAlign="center">
+            <MKTypography variant="button" color="text">
+              Don&apos;t have an account?{" "}
+              <MKTypography
+                component={Link}
+                to="/authentication/sign-up/cover"
+                variant="button"
+                color="info"
+                fontWeight="medium"
+                textGradient
+              >
+                Sign up
+              </MKTypography>
+            </MKTypography>
+          </MKBox>
+          {loginError && (
+            <MKBox mt={2} textAlign="center">
+              <MKTypography variant="body2" color="error">
+                {loginError}
+              </MKTypography>
+            </MKBox>
+          )}
+        </MKBox>
+      </MKBox>
+    </>
+  );
+}
+export default SignInBasic;

