+import React, { Component } from 'react';
+import axios from 'axios';
+
+class EditProduct extends Component {
+  state = {
+    product: {
+      // Initialize product fields as needed
+      nomProduit: '',
+      prixProduit: '',
+      categorie: '',
+      stockProduit: '',
+      imageProduit: '',
+    },
+  };
+
+  componentDidMount() {
+    const productId = this.props.match.params.id; // Get the product ID from URL
+    axios
+      .get(`http://localhost:8085/products/${productId}`)
+      .then((response) => {
+        this.setState({ product: response.data });
+      })
+      .catch((error) => {
+        console.error('Error fetching product details:', error);
+      });
+  }
+
+  handleInputChange = (e) => {
+    const { name, value } = e.target;
+    this.setState((prevState) => ({
+      product: {
+        ...prevState.product,
+        [name]: value,
+      },
+    }));
+  };
+
+  handleSubmit = (e) => {
+    e.preventDefault();
+    const productId = this.props.match.params.id; // Get the product ID from URL
+    axios
+      .put(`http://localhost:8085/products/${productId}`, this.state.product)
+      .then((response) => {
+        // Handle success, e.g., redirect to the product list page
+        this.props.history.push('/product-list'); // Change the URL to the product list page
+      })
+      .catch((error) => {
+        console.error('Error updating product:', error);
+      });
+  };
+
+  render() {
+    const { product } = this.state;
+
+    return (
+      <div>
+        <h2>Edit Product</h2>
+        <form onSubmit={this.handleSubmit}>
+          <div>
+            <label>Product Name:</label>
+            <input
+              type="text"
+              name="nomProduit"
+              value={product.nomProduit}
+              onChange={this.handleInputChange}
+            />
+          </div>
+          <div>
+            <label>Price:</label>
+            <input
+              type="number"
+              name="prixProduit"
+              value={product.prixProduit}
+              onChange={this.handleInputChange}
+            />
+          </div>
+          <div>
+            <label>Category:</label>
+            <input
+              type="text"
+              name="categorie"
+              value={product.categorie}
+              onChange={this.handleInputChange}
+            />
+          </div>
+          <div>
+            <label>Stock:</label>
+            <input
+              type="number"
+              name="stockProduit"
+              value={product.stockProduit}
+              onChange={this.handleInputChange}
+            />
+          </div>
+          <div>
+            <label>Image URL:</label>
+            <input
+              type="text"
+              name="imageProduit"
+              value={product.imageProduit}
+              onChange={this.handleInputChange}
+            />
+          </div>
+          <button type="submit">Save Changes</button>
+        </form>
+      </div>
+    );
+  }
+}
+
+export default EditProduct;

