    prixProduit: '',
    categorie: '',
    imageProduit: '',
    stockProduit: '',
  };

  componentDidMount() {
    const productId = this.props.match.params.id;

    axios.get(`http://your-api-url/products/${productId}`)
      .then(response => {
        this.setState({ product: response.data });
      })
      .catch(error => {
        console.error('Error fetching product:', error);
      });
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value });
  }

  handleSubmit = (e) => {
    e.preventDefault();

    const updatedProduct = {
      nomProduit: this.state.nomProduit,
      prixProduit: parseFloat(this.state.prixProduit),
      categorie: this.state.categorie,
      imageProduit: this.state.imageProduit,
      stockProduit: parseInt(this.state.stockProduit, 10),
    };

    const productId = this.props.match.params.id;

    axios.put(`http://your-api-url/products/${productId}`, updatedProduct)
      .then(response => {
        console.log('Product updated:', response.data);
        // You can redirect to the product list page or perform other actions
      })
      .catch(error => {
        console.error('Error updating product:', error);
      });
  }

  render() {
    return (
      <div>
        <h2>Edit Product</h2>
        <form onSubmit={this.handleSubmit}>
          {/* Pre-fill form fields with existing product data */}
          <div>
            <label>Product Name:</label>
            <input
              type="text"
              name="nomProduit"
              value={this.state.nomProduit}
              onChange={this.handleChange}
            />
          </div>
          {/* Include other form fields for prixProduit, categorie, imageProduit, stockProduit */}
          <button type="submit">Update Product</button>
        </form>
      </div>
    );
  }
}

export default EditProduct;
